import React, { useState } from 'react';

export default function App() {
	const questions = [
		{
			questionText: 'What is the make of the original Terminator',
			answerOptions: [
				{ answerText: 'T-1000', isCorrect: false },
				{ answerText: 'T-90', isCorrect: false },
				{ answerText: 'T-800', isCorrect: true },
				{ answerText: 'T-2', isCorrect: false },
			],
		},
		{
			questionText: 'Who created the X?',
			answerOptions: [
				{ answerText: 'Michael Dayson', isCorrect: false },
				{ answerText: 'Miles Dyson', isCorrect: true },
				{ answerText: 'David Miles', isCorrect: false },
				{ answerText: 'Mike Dyles', isCorrect: false },
			],
		},
		{
			questionText: 'In the Galleria Arcade, what game is John playng before the T-1000 finds him?',
			answerOptions: [
				{ answerText: 'Missile Command', isCorrect: true },
				{ answerText: 'Missile Launch', isCorrect: false },
				{ answerText: 'Missile Strike', isCorrect: false },
				{ answerText: 'Missile Battalion', isCorrect: false },
			],
		},
		{
			questionText: 'How many Harry Potter books are there?',
			answerOptions: [
				{ answerText: '1', isCorrect: false },
				{ answerText: '4', isCorrect: false },
				{ answerText: '6', isCorrect: false },
				{ answerText: '7', isCorrect: true },
			],
		},
	];

  const [currentQuestion, setCurrentQuestion] = useState(0);

  const answerButtonClick = (answerOption) => {
    const nextQuestion = currentQuestion + 1;
      setCurrentQuestion(nextQuestion);

      nextQuestion < questions.length ? setCurrentQuestion(nextQuestion) :
        setShowScore(true);
      }

const [showScore, setShowScore] = useState(false);

  return (  
		<div className='app'>
			{showScore ? (
				<div className='score-section'>You scored 1 out of {questions.length}</div>
			) : (
				<>        
					<div className='question-section'>

						<div className='question-count'>
							<span>Question 1</span>/{questions.length}
						</div>

						<div className='question-text'>{questions[currentQuestion].questionText}</div>
					</div>

					<div className='answer-section'>
						{questions[currentQuestion].answerOptions.map((answerOptions, index) => (
              <button onClick={() => answerButtonClick(answerOption.isCorrect)}>
                {answerOptions.answerText}</button>
            ))}
					</div>
				</>

			)}

		</div>
	);
}
